import Layout from '../components/layout'
import tps from '../utils/blog_types'
// import banner from '../images/banner'

export default class extends React.Component {
  static async getInitialProps({ req,query,jsonPageRes }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent,query,jsonPageRes }
  }

  state = {
      name:"1234"
  }

  componentDidMount(){
      console.log(this.props.jsonPageRes)
  }

  render() {
    let {name} = this.state;
    return (
      <div>
        <div style={{width:"100%",height:61,backgroundColor:"rgba(255,255,255,0.9)",position:"fixed",borderBottom:"1px solid #ddd",boxShadow:"0 1px 1px rgba(0,0,0,.04)",zIndex:999,lineHeight:"60px",fontSize:22,textIndent:"1em"}}>
          胡星个人博客
        </div>
        <div style={{height:75}}></div>
        <article style={{width:1000,margin:"0 auto"}}>
           <div className="left_box">
             <div className="left_item">
               <h2>关于</h2>
               <i className="about_me_i">
                <img src="/static/images/me.jpeg" style={{width:120,height:120}} />
               </i>
               <p style={{lineHeight: "24px","fontSize": 14}}>
                 <b>胡星</b>，93年出生，web前端工程师 && 服务端工程师，没什么特殊爱好，放假也是宅在家里，无聊的时候写写代码，研究研究技术方面的东西，对这种东西算一种热爱吧，但是我认为对于我来说，
                          只是选择了这一条路，选择了就要热爱，干上一行就得爱上一行，如果当初的选择不是互联网，我也会很热爱。
               </p>
             </div>
           </div>
           <div className="right_box"></div>
        </article>
        <style jsx>{`
          .left_box{
            width:300px;
            float:left;
          }
          .left_item{
            background: rgba(255,255,255,0.5);
            margin-bottom: 20px;
            overflow: hidden;
          }
          .left_item h2{
            color: #333;
            font-size: 14px;
            line-height: 30px;
            padding-left: 20px;
            background: #fff;
          }
          .right_box{
            width:680px;
            float:right;
          }
          .about_me_i{
            width: 120px;
            float: left;
            clear: left;
            margin-right: 10px;
            overflow: hidden;
          }
        `}</style>
        <style global jsx>{`
          body {
            font: 15px "Microsoft YaHei";
            color: #555;
            background: #efefef;
            line-height: 1.5;
            margin:0;
            padding:0;
          }
        `}</style>
      </div>
    )
  }
}
