import Layout from '../components/layout';
import * as api from '../apis/blog';

export default class extends React.Component {
  static async getInitialProps({ req,query,jsonPageRes }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent,query,jsonPageRes }
  }

  state = {

  }

  async componentDidMount(){
    var socket = new ReconnectingWebSocket("ws://localhost:14001/v1/ws/chatroom");
        socket.debug = true;
        socket.timeoutInterval = 5400;
        socket.onopen = function(){
          socket.send("发送数据");
        };

        socket.onmessage = function (evt) 
        { 
          var received_msg = evt.data;
          console.log(evt)
        };

        socket.onclose = function()
        { 
          console.log("链接已关闭")
        };
  }

  render() {
    const {blogDtail} = this.state
    return (
      <Layout>
        <div className="picbox">
          <ul>
            <li className="roomitem">
              <img src="http://www.yangqq.com/skin/850/images/text02.jpg" />         
              <div className="picinfo">
                <h3>关于golang那些事</h3>
                <span>房间人数：12人</span>
              </div>
            </li>
            <li className="roomitem" style={{float: "right"}}>
              <img src="http://www.yangqq.com/skin/850/images/text02.jpg" />         
              <div className="picinfo">
                <h3>生活琐事闲聊</h3>
                <span>房间人数：12人</span>
              </div>
            </li>
          </ul>
        </div>
        <style jsx>{`
         .roomitem{
            -webkit-transform: translatey(0);
            transform: translatey(0);
            opacity: 1;
            -webkit-transition: -webkit-transform 1s ease-in-out 0s,  opacity 1s ease-in-out 0s;transition: transform 1s ease-in-out 0s, opacity 1s ease-in-out 0s;
            -webkit-perspective: 1000;
            -webkit-backface-visibility: hidden;
          }
          .roomitem img{
            width:100%;
          }
          .picbox ul li i {
              margin: 10px;
              height: auto;
              overflow: hidden;
              display: block;
          }
          .picinfo h3 {
              border-bottom: #ccc 1px solid;
              padding: 10px 0;
              margin: 0 20px;
              font-size: 16px;
          }
          .picinfo span {
              padding: 10px 20px;
              line-height: 26px;
              color: #666;
              overflow: hidden;
              text-overflow: ellipsis;
              -webkit-box-orient: vertical;
              display: -webkit-box;
              -webkit-line-clamp: 2;
          }
          .picbox ul li {
              display: block;
              background: #FFF;
              margin: 0 0 20px 0;
              border: 1px #d9d9d9 solid;
              width:49%;
              float:left;
              cursor: pointer;
              transition:1s;
              -webkit-transition:1s;
          }
          .picbox ul li:hover{

          }
        `}</style>
      </Layout>
    )



  }
}
