import Layout from '../components/layout';
import * as api from '../apis/blog';
import pb from "../proto/ws_pb";

export default class extends React.Component {
  static async getInitialProps({ req,query,jsonPageRes }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent,query,jsonPageRes }
  }

  state = {
    socket:null,
    saytext:""
  }

  async componentDidMount(){
    const {roomid} = this.props.query;
    let {socket} = this.state;
    socket = new ReconnectingWebSocket(`ws://localhost:14001/v1/ws/chatroom?roomid=${roomid}`);
    socket.debug = true;
    socket.timeoutInterval = 5400;
    socket.onopen = function(){
      //socket.send("发送数据");
    };

    socket.onmessage = function (evt) 
    { 
      var received_msg = evt.data;
      console.log(evt)
    };

    socket.onclose = function()
    { 
      console.log("链接已关闭")
    };
    this.setState({socket});
  }

  async subMitComment(event){
    var e = event || window.event;
    if (e && e.keyCode == 13) { //回车键的键值为13
      let {saytext} = this.state;
      console.log(saytext);
      this.setState({saytext:""})
    }
  }

  render() {
    let {saytext} = this.state;
    return (
      <Layout>
        <div>
          <ul style={{listStyle:"none"}} className="chatlist">
            <li>
              <span>2020-04-29 00:00:00</span>
              <div style={{height:12}}></div>
              <div className="chatbox">阿萨答了几道阿斯达阿斯达垃圾袋了阿萨答了几道阿斯达阿斯达垃圾袋了阿萨答了几道阿斯达阿斯达垃圾袋了阿萨答了几道阿斯达阿斯达垃圾袋了</div>
            </li>
            <li>
              <span>2020-04-29 00:00:00</span>
              <div style={{height:12}}></div>
              <div className="chatbox">阿萨答了几道阿斯达阿斯达垃圾袋了</div>
            </li>
          </ul>
          <input className="chatcontent" value={saytext} onChange={(e)=>{
            this.setState({"saytext":e.target.value})
          }} onKeyUp={this.subMitComment.bind(this)} />
        </div>
        <style jsx>{`
          .chatbox{
            color: #333;
            border-radius: 5px;
          }
          .chatlist li:nth-child(1){
            margin-top: 0px;
          }
          .chatlist li {
            background: #fff;
            box-shadow: 3px 4px 4px #ccc;
            border-radius: 5px;
            padding: 15px;
            margin-top: 15px;
          }
          .chatlist li span{
            font-size: 14px;
          }
          .chatcontent{
            width: 97%;
            margin-top: 50px;
            color: #333;
            font-size: 18px;
            padding: 10px;
            border: solid #ccc;
          }
          .chatcontent:hover{
            outline: none;
          }
          .chatcontent:focus{
            outline: none;
          }

        `}</style>
      </Layout>
    )



  }
}
