import Layout from '../components/layout'
import Left from '../components/left'
import tps from '../utils/blog_types'
// import banner from '../images/banner'

export default class extends React.Component {
  static async getInitialProps({ req,query,jsonPageRes }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent,query,jsonPageRes }
  }

  state = {
      blogList:[],
      leftHeight:0
  }

  async componentDidMount(){
      console.log(this.props.jsonPageRes)
      let res = await fetch(`//momoman.cn/v1/blog/list?type=-1`)
      res = await res.json();
      console.log(res);
      this.setState({blogList:res.data,leftHeight:document.getElementById("left_box").offsetHeight})
  }

  render() {
    let {blogList,leftHeight} = this.state;
    return (
      <Layout>
        <article style={{width:1000,margin:"0 auto"}}>
           <Left />
           <div className="right_box">
             {
               blogList.map((item,k)=>(
                  <div className="right_item" key={k}>
                    {item.cover && <i><img src={item.cover} /></i>}
                    <h3>[ 顶 ] <span style={{color:"#222"}}>{item.name}</span></h3>
                    <p>{item.preface}</p>
                  </div>
               ))
             }
           </div>
        </article>
        <style jsx>{`
        .left_item .cation_item{
          margin-bottom: 10px;
          margin-left: 10px;
        }
        .search{
          border: 1px solid #000;
          background: #000;
          border-radius: 0 5px 5px 0;
          position: relative;
        }
        .search .input_submit {
              border: 0;
              background: 0;
              color: #fff;
              outline: none;
              position: absolute;
              top: 10px;
              right: 6%;
          }
          .search input.input_text {
              border: 0;
              line-height: 36px;
              height: 36px;
              width: 72%;
              padding-left: 10px;
              outline: none;
          }
          .right_item p {
            margin: 20px 0 0 0;
            line-height: 22px;
            overflow: hidden;
            text-overflow: ellipsis;
            -webkit-box-orient: vertical;
            display: -webkit-box;
            -webkit-line-clamp: 2;
          }
          .right_item h3{
            font-size: 16px;
            line-height: 25px;
            text-shadow: #FFF 1px 1px 1px;
            color: #de4949;
            margin:0;
          }
          .right_item i{
            width: 150px;
            display: block;
            max-height: 100px;
            overflow: hidden;
            float: right;
            margin-left: 20px;
          }
          .right_item{
            background: rgba(255,255,255,0.8);
            padding: 15px;
            overflow: hidden;
            color: #797b7c;
            margin-bottom: 20px;
          }
          .left_box{
            width:300px;
            float:left;
          }
          .left_item{
            background: rgba(255,255,255,0.5);
            margin-bottom: 20px;
            overflow: hidden;
          }
          .left_item ul li{
            width: 32%;
            overflow: hidden;
            float: left;
            height: 80px;
            margin-bottom: 2px;
            margin-right: 2px;
          }
          .left_item ul li img{
            width: 100%;
            height: 100%;
            -webkit-transition: all 0.5s;
            -moz-transition: all 0.5s;
            transition: all 0.5s;
            display:block;
          }
          .left_item h2{
            color: #333;
            font-size: 14px;
            line-height: 30px;
            padding-left: 20px;
            background: #fff;
            margin:0
          }
          .right_box{
            width:680px;
            float:right;
          }
          .about_me_i{
            width: 120px;
            float: left;
            clear: left;
            margin-right: 10px;
            overflow: hidden;
          }
        `}</style>
        <style global jsx>{`
          body {
            font: 15px "Microsoft YaHei";
            color: #555;
            background: #efefef;
            line-height: 1.5;
            margin:0;
            padding:0;
          }
        `}</style>
      </Layout>    

    )
  }
}
