import Layout from '../components/layout'
import {getTpValue} from '../utils/blog_types'
import moment from 'moment'
import {apiHost} from '../utils/config';
import protobuf from "../proto/blog_pb";
import axios from 'axios'
import readStream from '../utils/util'

export default class extends React.Component {
  static async getInitialProps({ req,query,jsonPageRes }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent,query,jsonPageRes }
  }

  state = {
      blogDtail:"",
      detailData:{}
  }

  async componentDidMount(){
      const {id} = this.props.query
      let res = await axios.get(`${apiHost}/v1/blog/detail?id=${id}`,{
        responseType: 'blob'
      })
      let data = await readStream(res.data);
      let message = protobuf.detailRes.deserializeBinary(data);
      data = message.toObject();
      let key = data.htmlTxtUrl.replace("http:"+apiHost + "/redources/","");
      let hres = await axios.get(`${apiHost}/tool/file/read?key=${key}`,{
        responseType: 'blob'
      })
      let hdata = await readStream(hres.data);
      let hmessage = protobuf.fileReadRes.deserializeBinary(hdata);
      hdata = hmessage.toObject();
      this.setState({blogDtail:hdata.txt,detailData:data})
  }

  render() {
    const {blogDtail,detailData} = this.state
    return (
      <Layout>
          <div style={{background:"#fff",padding: "0 30px"}}>
            <h3 className="news_title">{detailData.name}</h3>
            <div className="bloginfo">
              <ul>
                <li className="author"></li>
                <li className="lmname">{getTpValue(detailData.type)}</li>
                <li className="timer">{moment(detailData.createTime).format("YYYY-MM-DD")}</li>
                <li className="view">{detailData.view} 人已阅读</li>
              </ul>
            </div>
            <div style={{clear:'both'}}></div>
            <div className="news_about">
              <strong>简介</strong> {detailData.preface}
            </div>
            <div dangerouslySetInnerHTML={{__html: blogDtail}} className="markdown-body editormd-preview-container" previewcontainer="true" style={{width:"auto"}}></div>
            <div className="share" style={{padding: "20px"}}>
             <p className="diggit">
               <a>很赞哦！</a>(<b>79</b>)
             </p>
            </div>
            <div className="nextinfo">
              <p>上一篇：<a href="/diary/18.html">一个SEO站长分享个人博客产品网赚建站经历全过程</a></p>
              <p>下一篇：<a href="/diary/">返回列表</a></p>
            </div>

            <div className="news_pl">
              <h2>文章评论</h2>
              <div className="gbko"> 
                <div className="fb">
                  <ul>
                    <p className="fbtime"><span>2019-08-12 12:05:07</span> 2121</p>
                    <p className="fbinfo">33232</p>
                  </ul>
                </div>                   
                <div className="fb">
                  <ul>
                    <p className="fbtime"><span>2019-02-17 16:02:42</span> 程序员小张</p>
                    <p className="fbinfo">没兴趣就不要入门这一行了。</p>
                  </ul>
                </div>
                <div id="plpost">
                  <p className="saying">
                    <span><a href="/e/pl/?classid=3&amp;id=19">共有34条评论</a></span>
                    来说两句吧...
                  </p>
                  <p className="yzm">
                    <span>验证码:</span>
                    <input name="key" type="text" className="inputText" size="16" />
                    <img src="/e/ShowKey/?v=pl" align="absmiddle" name="plKeyImg" id="plKeyImg" title="看不清楚,点击刷新" /> 
                  </p>
                  <input name="nomember" type="hidden" id="nomember" value="1" checked="checked" />
                  <textarea name="saytext" rows="6" id="saytext"></textarea>
                  <input name="imageField" type="submit" value="提交" />
                  {/* <input name="id" type="hidden" id="id" value="19" />
                  <input name="classid" type="hidden" id="classid" value="3" />
                  <input name="enews" type="hidden" id="enews" value="AddPl" />
                  <input name="repid" type="hidden" id="repid" value="0" />
                  <input type="hidden" name="ecmsfrom" value="/diary/19.html" /> */}
                </div>
              </div>
            </div>

          </div>
          <style jsx>{`
          .yzm {
              margin: 10px 10px 10px 0;
          }
          #plpost input[type="submit"] {
            display: block;
            background: #303030;
            color: #fff;
            border: 0;
            line-height: 30px;
            padding: 0 20px;
            border-radius: 5px;
            float: right;
            margin-bottom: 20px;
            margin-top: 13px;
            margin-right: 1%;
          }
          #saytext {
              width: 99%;
              outline: none;
              resize: none;
          }
          .saying span {
                float: right;
            }
           .saying{
            line-height: 30px;
            color: #a9a6a6;
           } 
           .news_pl h2{
            border-bottom: #000 2px solid;
            line-height: 40px;
            font-size: 14px;
            padding-left: 10px;
            color: #000;
           }
           .fbtime{
            float: right;
            color: #999;
            font-size: 12px;
            width: 66px;
            overflow: hidden;
            white-space: nowrap;
           }
           .fbtime span{
            float: right;
            color: #999;
            font-size: 12px;
            width: 66px;
